    """Returns `dm_path` without the `.dm` part.

    {{ðŸ¦” If `dm_path` is "foo/bar/quux.dm.py", returns "foo/bar/quux.py"}}
    """
    # âœ¨ PathAndValidator output path
    return pathlib.Path(self.dm_path.parent /
                        self.dm_path.name.replace(".dm.", "."))
